import React, { useState } from 'react';
import axios from 'axios';
import './Register.css';

const App = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    email: '',
    phoneNumber: ''
  });

  const [message, setMessage] = useState('');
  const [validUsername, setValidUsername] = useState(false);
  const [validPassword, setValidPassword] = useState(false);
  const [validPasswordMatch, setValidPasswordMatch] = useState(false);
  const [validEmail, setValidEmail] = useState(false);
  const [validPhoneNumber, setValidPhoneNumber] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if(formData.username.length < 6){
        setValidUsername(t)
        return;
    }
    if (formData.password !== formData.confirmPassword) {
      setMessage('Passwords do not match.');
      return;
    }

    if (formData.password.length < 6) {
      setMessage('Password must have at least 6 characters.');
      return;
    }

    // Frontend validation for username length
    if (formData.username.length < 6) {
      setMessage('Username must have at least 6 characters.');
      return;
    }

    // Frontend validation for phone number length
    if (formData.phoneNumber.length !== 11) {
      setMessage('Phone number must have exactly 11 digits.');
      return;
    }

    try {

      const response = await axios.post('http://localhost:8000/register', {
        username: formData.username,
        password: formData.password,
        email: formData.email,
        phoneNumber: formData.phoneNumber

      });
      setMessage(response.data.message);
    } catch (error) {
      console.log(error.response.data);
      setMessage(error.response.data.detail);
    }
  };

  return (
    <div>
      <h1>User Registration</h1>
      <form className = 'form' onSubmit={handleSubmit}>
        <div>
          <label>Username:</label>
          <input type="text" name="username" value={formData.username} onChange={handleChange} required />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange} required />
        </div>
        <div>
          <label>Confirm Password:</label>
          <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Phone Number:</label>
          <input type="tel" name="phoneNumber" value={formData.phoneNumber} onChange={handleChange} required />
        </div>
        <button type="submit">Register</button>
      </form>
      {message && <p >{message}</p>}
    </div>
  );
};

export default App;
